#ifndef ROOT_RVersion
#define ROOT_RVersion

/* Version information automatically generated by installer. */

/*
 * These macros can be used in the following way:
 *
 *    #if ROOT_VERSION_CODE >= ROOT_VERSION(2,23,4)
 *       #include <newheader.h>
 *    #else
 *       #include <oldheader.h>
 *    #endif
 *
*/

#define ROOT_RELEASE "5.24/00b"
#define ROOT_RELEASE_DATE "Oct 11 2009"
#define ROOT_RELEASE_TIME "22:07:10"
#define ROOT_SVN_REVISION 30658
#define ROOT_SVN_BRANCH "branches/v5-24-00-patches"
#define ROOT_VERSION_CODE 333824
#define ROOT_VERSION(a,b,c) (((a) << 16) + ((b) << 8) + (c))

#endif
