#ifndef ROOT_RVersion
#define ROOT_RVersion

/* Version information automatically generated by installer. */

/*
 * These macros can be used in the following way:
 *
 *    #if ROOT_VERSION_CODE >= ROOT_VERSION(2,23,4)
 *       #include <newheader.h>
 *    #else
 *       #include <oldheader.h>
 *    #endif
 *
*/

#define ROOT_RELEASE "5.30/00"
#define ROOT_RELEASE_DATE "Jun 27 2011"
#define ROOT_RELEASE_TIME "15:00:08"
#define ROOT_SVN_REVISION 39979
#define ROOT_SVN_BRANCH "branches/v5-30-00-patches"
#define ROOT_VERSION_CODE 335360
#define ROOT_VERSION(a,b,c) (((a) << 16) + ((b) << 8) + (c))

#endif
