#ifndef ROOT_RVersion
#define ROOT_RVersion

/* Version information automatically generated by installer. */

/*
 * These macros can be used in the following way:
 *
 *    #if ROOT_VERSION_CODE >= ROOT_VERSION(2,23,4)
 *       #include <newheader.h>
 *    #else
 *       #include <oldheader.h>
 *    #endif
 *
*/

#define ROOT_RELEASE "5.28/00a"
#define ROOT_RELEASE_DATE "Feb 21 2011"
#define ROOT_RELEASE_TIME "12:44:24"
#define ROOT_SVN_REVISION 38168
#define ROOT_SVN_BRANCH "branches/v5-28-00-patches"
#define ROOT_VERSION_CODE 334848
#define ROOT_VERSION(a,b,c) (((a) << 16) + ((b) << 8) + (c))
#define ROOT_FULL_VERSION_CODE 85721185
#define ROOT_FULL_VERSION(a,b,c,p) (((a) << 24) + ((b) << 16) + ((c) << 8) + (p))

#endif
